package loopsprg;

public class Whileloopprg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i=0;
		int j=0;
				
		while(i<10)
		{
			System.out.println(i);
			i++;
		}
		
		do
		{
			
			System.out.println("value of j"  +j);
			j++;
		
		}while(j<10);
		
	}

}
